<template>
    <div class="light" @click="toggle">
        <!-- <div class="light__icon" :class="{ 'active': device.isActive}"></div> -->
        <icon-light :active="device.isActive"></icon-light>
        <div class="light__description">{{ device.room }}<br>
            <!-- <span class="countdown" v-if="light.autoOffTicker">
                {{ light.autoOffTicker.remaining }}</span> -->
        </div>
    </div>
</template>

<script>
// import '@/utils/countdown.js';
import IconLight from '~/components/icons/devices/IconLight'

export default {
  name: "Light",
  components: { IconLight },
  props: {
    'device': {
      type: Object,
      required: true
    },
    'passive': {
      type: Boolean,
      default: false
    }
  },
  mounted() {},
  methods: {
    toggle() {
      if (!this.passive) {
        this.device.toggle();
      }
    }
  }
};
</script>


